mapper.select:
SELECT 	* 
FROM 	EMPLOYEE
WHERE 	EMPID = #{empid} AND EMPPWD = #{emppwd}

mapper.delete:
DELETE FROM 	FAVORITE
WHERE  			EMPID = #{empid}
AND 	 		EMPIDFAV = #{empidfav}

mapper.update:
UPDATE EMPLOYEE SET EMPID=#{empid}, EMPPWD=#{emppwd}, EMPNAME=#{empname}, EMPPHONE=#{empphone}, EMPMAIL=#{empmail}, EMPLOC=#{emploc},
EMPIMG=#{empimg, jdbcType=VARCHAR},
DEPTID=(SELECT DEPTID FROM DEPARTMENT WHERE DEPTNAME = #{deptname} AND DIVID = (SELECT DIVID FROM DIVISION WHERE DIVNAME = #{divname} AND CHECKYN = 'Y')) WHERE EMPID=#{empid}

UPDATE 	WORK 
SET		AMLOC = #{loc} , PMLOC = #{loc}
WHERE 	EMPID = #{empid} AND WORKDATE=(SELECT TO_CHAR(SYSDATE, 'yyyy-MM-DD') FROM DUAL)

UPDATE 	WORK 
SET		PMLOC = #{loc}
		WHERE EMPID = #{empid} AND WORKDATE=(SELECT TO_CHAR(SYSDATE, 'yyyy-MM-DD') FROM DUAL)
